import React, { Component } from 'react';

class CarouselSlide extends Component {
  render() {
    return (
      <li
        className={
          this.props.index == this.props.activeIndex
            ? "carousel__slide carousel__slide__active"
            : "carousel__slide"
        }
      >
        <div className="carousel-slide__content">
          <img src={this.props.slide.image_url} />
        </div>

        <div className="carousel-slide__source_container">
          <strong className="carousel-slide__author">
            {this.props.slide.title}
          </strong><br />
          <div className="carousel-slide__source">
            {this.props.slide.subtitle}
          </div>
          <div className="carousel-slide__source">
            {this.props.slide.text}
          </div>
        </div>
      </li>
    );
  }
}

export default CarouselSlide;
